import path from 'path';

import fastify from 'fastify';
import now from 'fastify-now';
import startCore from './core';

// Load env vars
import loadConfig from './lib/config';
loadConfig();

export async function createServer() {
  const server = fastify({
    logger: {
      level: process.env.LOG_LEVEL,
    },
  });

  server.register(now, {
    routesFolder: path.join(__dirname, './routes'),
  });

  await server.ready();
  return server;
}

export async function startServer() {
  process.on('unhandledRejection', (err) => {
    console.error(err);
    process.exit(1);
  });

  const server = await createServer();
  await server.listen(+process.env.API_PORT, process.env.API_HOST);

  for (const signal of ['SIGINT', 'SIGTERM']) {
    process.on(signal, () =>
      server.close().then((err) => {
        console.log(`close application on ${signal}`);
        process.exit(err ? 1 : 0);
      }),
    );
  }
}

if (process.env.NODE_ENV !== 'test') {
  startServer();
}

import path from 'path';
import envSchema from 'env-schema';
import { Static, Type } from '@sinclair/typebox';

export enum NodeEnv {
  development = 'development',
  test = 'test',
  production = 'production',
}

const ConfigSchema = Type.Object({
  NODE_ENV: Type.Enum(NodeEnv),
  API_HOST: Type.String(),
  API_PORT: Type.String(),
  VRAM: Type.Optional(Type.Number()),
});

export type Config = Static<typeof ConfigSchema>;

export default function loadConfig(): void {
  const result = require('dotenv').config({
    path: path.join(__dirname, `../../.env.${process.env.NODE_ENV ?? 'development'}`),
  });

  if (result.error) {
    throw new Error(result.error);
  }

  envSchema({
    data: result.parsed,
    schema: Type.Strict(ConfigSchema),
  });
}

import { NowRequestHandler } from 'fastify-now';
import { Type } from '@sinclair/typebox';
import prisma from '../../lib/prisma';

type Post = NowRequestHandler<{
  Body: { tokenId: string };
}>;

export const POST: Post = async function (req, res) {
  const { tokenId } = req.body;

  await prisma.queue.create({ data: { tokenId } });

  res.code(202);

  return { success: true };
};

POST.opts = {
  schema: {
    body: {
      tokenId: Type.String(),
    },
    response: {
      202: Type.Object({
        success: Type.Boolean({ default: true }),
      }),
      400: Type.Object({
        success: Type.Boolean({ default: false }),
      }),
      500: Type.Object({
        success: Type.Boolean({ default: false }),
      }),
    },
  },
};

import { PrismaClient } from '@prisma/client';
import { execSync } from 'child_process';
import { join } from 'path';
import { v4 as uuidv4 } from 'uuid';

const prisma = new PrismaClient();

export default prisma;

const cwd = join(__dirname, '..', '..');

export const startPrismaTest = async () => {
  const uuid = uuidv4();
  await prisma.$disconnect();

  process.env.DATABASE_URL = process.env.DATABASE_URL + `-test-${uuid}`;

  const cwd = join(__dirname, '..', '..');
  execSync('npx prisma db push --force-reset', {
    cwd,
    env: process.env,
  });

  await prisma.$connect();
};

export const destroyPrismaTest = async () => {
  prisma.$disconnect();
};

import { NowRequestHandler } from 'fastify-now';
import { Type } from '@sinclair/typebox';

export const GET: NowRequestHandler = async function () {
  return { hello: 'world' };
};

GET.opts = {
  schema: {
    response: {
      200: Type.Object({
        hello: Type.String(),
      }),
    },
  },
};
